mapper.select:
select

from emp
where  dept_id = #{deptId,jdbcType=VARCHAR} and ranks = #{ranks,jdbcType=INTEGER}  and name like  #{name,jdbcType=VARCHAR}

select

from emp
where  parent_id = #{parentId,jdbcType=VARCHAR}  and name like  #{name,jdbcType=VARCHAR}

select

from emp
where 1 = 1   and name like  #{name,jdbcType=VARCHAR}  and dept_id = #{deptId,jdbcType=VARCHAR}

